 * For more details on building Java & JVM projects, please refer to https://docs.gradle.org/9.2.0/userguide/building_java_projects.html in the Gradle documentation.
 */

plugins {
    // Apply the org.jetbrains.kotlin.jvm Plugin to add support for Kotlin.
    alias(libs.plugins.kotlin.jvm)

    // Apply the java-library plugin for API and implementation separation.
    `java-library`

    // Apply the maven-publish plugin for publishing to Maven repositories.
    `maven-publish`
}

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    // Use JUnit Jupiter for testing.
    testImplementation(libs.junit.jupiter)
    testImplementation("org.jetbrains.kotlin:kotlin-test-junit5")
    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    // This dependency is exported to consumers, that is to say found on their compile classpath.
    api("org.web3j:core:4.14.0")

    // This dependency is used internally, and not exposed to consumers on their own compile classpath.
    implementation("io.github.microutils:kotlin-logging:3.0.0")
    implementation("org.jetbrains.kotlinx:kotlinx-coroutines-core:1.8.0")

    // testImplementation("com.redis:testcontainers-redis:2.2.2")
    // testImplementation("org.testcontainers:junit-jupiter")
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(21)
    }
}

tasks.named<Test>("test") {
    useJUnitPlatform {
        excludeTags("stress")
    }

    testLogging {
        events("passed", "failed")
        showStandardStreams = true
    }
}

publishing {
    publications {
        create<MavenPublication>("mavenJava") {
            from(components["java"])
            groupId = "com.psychoplasma.nonceq"
            artifactId = "nonceq"
            version = project.version.toString()

            pom {
                name = "nonceq"
                description = "Nonce queuing manager for nonce-based transaction. Simple LRU queue with reuse and discarding features."
                url = "https://github.com/psychoplasma/nonceq"

                licenses {
                    license {
                        name = "The Apache License, Version 2.0"
                        url = "http://www.apache.org/licenses/LICENSE-2.0.txt"
                    }
                }

                developers {
                    developer {
                        name = "Mustafa Morca"
                    }
                }

                scm {
                    connection = "scm:git:git://github.com/psychoplasma/nonceq.git"
                    developerConnection = "scm:git:ssh://git@github.com:psychoplasma/nonceq.git"
                    url = "https://github.com/psychoplasma/nonceq"
                }
            }
        }
    }

    repositories {
        maven {
            name = "GitHubPackages"
            url = uri("https://maven.pkg.github.com/psychoplasma/nonceq")
            credentials {
                username = System.getenv("GITHUB_ACTOR") ?: project.findProperty("gpr.user") as? String
                password = System.getenv("GITHUB_TOKEN") ?: project.findProperty("gpr.key") as? String
            }
        }
    }
}
